// Genera el contenido del PDF
function generarContenido(pdf) {
  pdf.setFont("courier");
  pdf.setFontSize(13);


  // Agrega el logotipo
  pdf.addImage(logo, "PNG", 150, 10, 0, 40);

  //textos
  pdf.text('Bogotá, ' +Fecha, 10, 10 )
  pdf.text('Frescas y Maduradas', 80, 80);
  pdf.text('Certifica', 95, 90);
  pdf.text('Que el(a) señor(@)', 10, 120);
  pdf.text('identificado con CC.', 145, 120);
  pdf.text('de Bogotá, labora con nosotros como', 48, 130);
  pdf.text('durante un tiempo de', 10 ,140);
  pdf.text('de manera eficiente', 90, 140);

  // Agrega el nombre del empleado
  pdf.text(Nombre, 60, 120);

  // Agrega la identificación del empleado
  pdf.text("No:"+ Identificación, 10, 130);

  // Agrega el puesto del empleado
  pdf.text(Puesto+ ",", 148, 130);

  // Agrega el tiempo de servicio
  pdf.text(Tiempo, 68, 140);

  // Agrega el icono de la empresa
  pdf.addImage(icono, "PNG", 160, 50, 0, 150);
}

// Valida el formulario
function validarFormulario() {
  const camposObligatorios = ["Nombre", "Identificación", "Puesto", "Fecha", "Términos y condiciones"];
  for (const campo of camposObligatorios) {
    const valorCampo = document.querySelector(`input[name="${campo}"]`).value;
    if (valorCampo === "") {
      alert(`El campo ${campo} es obligatorio.`);
      return false;
    }
  }
  return true;
}

// Genera el PDF
function generarPDF() {
  // Crea un nuevo PDF
  const pdf = new jsPDF({ orientation: "portrait", unit: "mm", format: "letter" });

  // Genera el contenido del PDF
  generarContenido(pdf);

  // Descarga el PDF
  pdf.save("Certificado.pdf");
}

// Escucha el evento click del botón de envío
document.getElementById("submit").addEventListener("click", generarPDF);


